package testabilities

import (
	"testing"

	"github.com/bsv-blockchain/go-sdk/chainhash"
	"github.com/bsv-blockchain/go-sdk/transaction"
	"github.com/stretchr/testify/require"
)

const DefaultBlockHeight uint32 = 848372

const (
	DefaultBEEF = ""
	DefaultTxID = "27c8f37851aabc468d3dbb6bf0789dc398a602dcb897ca04e7815d939d621595"
)

const (
	DefaultARCCallbackToken = "valid_arc_callback_token"
	DefaultARCAPIKey        = "valid_arc_api_key"
)

func NewTestMerklePath(t *testing.T) string {
	t.Helper()

	tx, err := transaction.NewTransactionFromBEEFHex(DefaultBEEF)
	require.NoError(t, err)
	require.NotNil(t, tx)

	return tx.MerklePath.Hex()
}

func NewTxID(t *testing.T) string {
	t.Helper()

	txID, err := chainhash.NewHashFromHex(DefaultTxID)
	require.NoError(t, err)

	return txID.String()
}
